      nationalId: user.nationalId,
      name: user.name,
      eventType: finalEventType,
      eventDate,
      eventDetails,
      eventLocation,
      approxAttendees,
      budget,
      contactPhone,
      specialReq,
      status: 'Agent will be in touch soon'
