import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import Login from './components/Login';
import Signup from './components/Signup';
import Dashboard from './components/Dashboard';

function AppContent() {
  const [user, setUser] = useState(null);
  const [showRefreshMessage, setShowRefreshMessage] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // Load user data from localStorage on app start
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    // Check if user just logged out (localStorage has 'loggedOut' flag)
    const loggedOut = localStorage.getItem('loggedOut');
    if (loggedOut === 'true') {
      setShowRefreshMessage(true);
      localStorage.removeItem('loggedOut');
    }
  }, []);

  return (
    <div className="App">
      {showRefreshMessage && location.pathname === '/' && (
        <div className="refresh-message">
          <div className="refresh-message-content">
            <span className="refresh-icon">ðŸ”„</span>
            <p>You have been logged out. Please refresh the page to see the login and signup options.</p>
            <button
              className="refresh-close-btn"
              onClick={() => setShowRefreshMessage(false)}
              title="Close message"
            >
              âœ•
            </button>
          </div>
        </div>
      )}
      <Navbar user={user} setUser={setUser} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/signup" element={<Signup setUser={setUser} />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
