import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [eventType, setEventType] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [eventDetails, setEventDetails] = useState('');
  const [bookings, setBookings] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard');
  const navigate = useNavigate();

  const testimonials = [
    { name: "Alice Johnson", testimonial: "Snakepiece made my wedding unforgettable! Every detail was perfect.", image: "./images/1.jpg" },
    { name: "Bob Smith", testimonial: "Corporate event went smoothly thanks to their expertise.", image: "./images/2.jpg" },
    { name: "Carol Davis", testimonial: "Birthday party for my kids was a blast!", image: "./images/3.webp" },
    { name: "David Wilson", testimonial: "Conference planning was seamless.", image: "./images/1.jpg" },
    { name: "Eva Brown", testimonial: "They turned my vision into reality.", image: "./images/2.jpg" },
    { name: "Frank Miller", testimonial: "Outstanding service and creativity.", image: "./images/3.webp" },
    { name: "Grace Lee", testimonial: "Highly recommend for any event!", image: "./images/1.jpg" },
    { name: "Henry Taylor", testimonial: "Professional and fun team.", image: "./images/2.jpg" },
    { name: "Ivy Chen", testimonial: "Exceeded all expectations.", image: "./images/3.webp" },
    { name: "Jack Anderson", testimonial: "Made my graduation memorable.", image: "./images/1.jpg" },
    { name: "Kathy White", testimonial: "Fantastic attention to detail.", image: "./images/2.jpg" },
    { name: "Liam Harris", testimonial: "Event of a lifetime!", image: "./images/3.webp" },
    { name: "Mia Clark", testimonial: "Stress-free planning experience.", image: "./images/1.jpg" },
    { name: "Noah Lewis", testimonial: "Creative and innovative.", image: "./images/2.jpg" },
    { name: "Olivia Walker", testimonial: "Worth every penny.", image: "./images/3.webp" },
    { name: "Paul Hall", testimonial: "Amazing vendors and coordination.", image: "./images/1.jpg" },
    { name: "Quinn Young", testimonial: "They made it magical.", image: "./images/2.jpg" },
    { name: "Rachel King", testimonial: "Top-notch service.", image: "./images/3.webp" },
    { name: "Sam Wright", testimonial: "Unparalleled professionalism.", image: "./images/1.jpg" },
    { name: "Tina Adams", testimonial: "Will use again for sure!", image: "./images/2.jpg" },
    { name: "Uma Patel", testimonial: "Dream team for events.", image: "./images/3.webp" },
    { name: "Victor Reed", testimonial: "Flawless execution.", image: "./images/1.jpg" },
    { name: "Wendy Foster", testimonial: "Heartfelt thanks!", image: "./images/2.jpg" },
    { name: "Xander Gray", testimonial: "Beyond impressed.", image: "./images/3.webp" },
    { name: "Yara Nelson", testimonial: "Perfect from start to finish.", image: "./images/1.jpg" }
  ];

  useEffect(() => {
    const loggedUser = localStorage.getItem('user');
    if (!loggedUser) {
      navigate('/login');
    } else {
      setUser(JSON.parse(loggedUser));
      // Load bookings from API
      fetch('http://localhost:3000/bookings')
        .then(response => response.json())
        .then(data => {
          const userBookings = data.filter(booking => booking.userId === JSON.parse(loggedUser).id);
          // Update any existing 'Pending' statuses to the new message
          const updatedBookings = userBookings.map(booking => ({
            ...booking,
            status: booking.status === 'Pending' ? 'Agent will be in touch soon' : booking.status
          }));
          setBookings(updatedBookings);
        })
        .catch(error => console.error('Error fetching bookings:', error));
    }
  }, [navigate]);

  const handleBooking = (e) => {
    e.preventDefault();
    const newBooking = {
      id: Date.now(),
      userId: user.id,
      eventType,
      eventDate,
      eventDetails,
      status: 'Agent will be in touch soon'
    };
    // POST new booking to API
    fetch('http://localhost:3000/bookings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newBooking),
    })
      .then(response => response.json())
      .then(data => {
        setBookings([...bookings, data]);
        // Reset form
        setEventType('');
        setEventDate('');
        setEventDetails('');
        alert('Booking submitted successfully!');
      })
      .catch(error => console.error('Error creating booking:', error));
  };

  const handleDeleteBooking = (id) => {
    // DELETE booking from API
    fetch(`http://localhost:3000/bookings/${id}`, {
      method: 'DELETE',
    })
      .then(() => {
        const updatedBookings = bookings.filter(booking => booking.id !== id);
        setBookings(updatedBookings);
      })
      .catch(error => console.error('Error deleting booking:', error));
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/');
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="dashboard">
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Snakepiece event house Kenya</h2>
        </div>
        <nav className="sidebar-nav">
          <ul>
            <li className="active">
              <button>Dashboard</button>
            </li>
            <li>
              <button>Booking Hub</button>
            </li>
            <li>
              <button>Vendor Network</button>
            </li>
            <li>
              <button>Event Calendar</button>
            </li>
          </ul>
        </nav>
      </div>
      <div className="main-content">
        <header className="dashboard-header">
          <h1>Welcome back, {user.name}</h1>
          <div className="profile-icon">
            <span>{user.name.charAt(0).toUpperCase()}</span>
          </div>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </header>

        <div className="profile-section">
          <div className="profile-left">
            <h2>Your Profile</h2>
            <div className="profile-info">
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>National ID:</strong> {user.nationalId}</p>
            </div>
            <p className="profile-note">Profile details cannot be edited.</p>
          </div>
          <div className="profile-card">
            <div className="card-background">
              <div className="shape shape1"></div>
              <div className="shape shape2"></div>
              <div className="shape shape3"></div>
            </div>
            <div className="card-content">
              <h3>DON'T FORGET TO BOOK YOUR NEXT PARTY BEFORE IT TURNS INTO A SOLO DANCE!</h3>
              <button className="setup-btn">BOOK NOW</button>
            </div>
          </div>
        </div>

        <div className="dashboard-content">
          <div className="booking-section">
            <h2>Book Your Event</h2>
            <form onSubmit={handleBooking} className="booking-form">
              <div className="form-group">
                <label>Event Type</label>
                <select value={eventType} onChange={(e) => setEventType(e.target.value)} required>
                  <option value="">Select Event Type</option>
                  <option value="Wedding">Wedding</option>
                  <option value="Corporate">Corporate Event</option>
                  <option value="Birthday">Birthday Party</option>
                  <option value="Conference">Conference</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="form-group">
                <label>Event Date</label>
                <input
                  type="date"
                  value={eventDate}
                  onChange={(e) => setEventDate(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label>Event Details</label>
                <textarea
                  value={eventDetails}
                  onChange={(e) => setEventDetails(e.target.value)}
                  placeholder="Describe your event requirements..."
                  required
                ></textarea>
              </div>
              <button type="submit" className="book-btn">Book Event</button>
            </form>
          </div>

          <div className="bookings-section">
            <h2>Your Bookings</h2>
            {bookings.length === 0 ? (
              <p>No bookings yet. Book your first event!</p>
            ) : (
              <div className="bookings-list">
                {bookings.map(booking => (
                  <div key={booking.id} className="booking-card">
                    <h3>{booking.eventType}</h3>
                    <p><strong>Date:</strong> {booking.eventDate}</p>
                    <p><strong>Details:</strong> {booking.eventDetails}</p>
                    <span className={`status ${booking.status.toLowerCase().replace(/\s+/g, '-')}`}>{booking.status}</span>
                    <button onClick={() => handleDeleteBooking(booking.id)} className="delete-btn">Delete</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="services-section">
          <h2>Our Services</h2>
          <div className="services-list">
            <div className="service-item">
              <h3>PA Systems</h3>
              <p>Professional sound systems for clear audio at your events.</p>
            </div>
            <div className="service-item">
              <h3>MC Services</h3>
              <p>Experienced Master of Ceremonies to host and manage your event.</p>
            </div>
            <div className="service-item">
              <h3>Videography</h3>
              <p>High-quality video recording and editing for your special moments.</p>
            </div>
            <div className="service-item">
              <h3>Photography</h3>
              <p>Capturing beautiful memories with professional photography services.</p>
            </div>
            <div className="service-item">
              <h3>Tents</h3>
              <p>Spacious and elegant tents for outdoor events and gatherings.</p>
            </div>
            <div className="service-item">
              <h3>Cars</h3>
              <p>Luxury vehicle rentals for transportation and special occasions.</p>
            </div>
            <div className="service-item">
              <h3>Event Planning</h3>
              <p>Comprehensive event management and coordination services.</p>
            </div>
            <div className="service-item">
              <h3>Catering</h3>
              <p>Delicious food and beverage services tailored to your event needs.</p>
            </div>
            <div className="service-item">
              <h3>Decorations</h3>
              <p>Creative and thematic decorations to enhance your event atmosphere.</p>
            </div>
            <div className="service-item">
              <h3>Lighting</h3>
              <p>Professional lighting setups for ambiance and visibility.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
