  const handleDateChange = (e) => {
    const selectedDateValue = e.target.value;
    setEventDate(selectedDateValue);

    if (!selectedDateValue) {
      setDateError('');
      return;
    }

    const selectedDate = new Date(selectedDateValue);
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Compare date part only

    if (selectedDate < today) {
      setDateError('You must choose a future date for the event.');
    } else if (selectedDate.getTime() === today.getTime()) {
      setDateError('We cannot schedule and arrange your event on the same day. Please insert a future date.');
    } else {
      setDateError('');
    }
  };

  const handleEditDateChange = (e) => {
    const selectedDateValue = e.target.value;
    setEditEventDate(selectedDateValue);

    if (!selectedDateValue) {
      setEditDateError('');
      return;
    }

    const selectedDate = new Date(selectedDateValue);
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Compare date part only

    if (selectedDate < today) {
      setEditDateError('You must choose a future date for the event.');
    } else if (selectedDate.getTime() === today.getTime()) {
      setEditDateError('We cannot schedule and arrange your event on the same day. Please insert a future date.');
    } else {
      setEditDateError('');
    }
  };

  const handleBooking = async (e) => {
    e.preventDefault();

    // Re-validate date on submission to be absolutely sure
    if (dateError) {
      // The error popup is already showing, so we just stop.
      return;
    }
    if (!eventDate) {
      setDateError('Please select an event date.');
      return;
    }
    setIsLoading(true);
    const finalEventType = eventType === 'Other' && customEventType ? customEventType : eventType;
    const newBooking = {
      userId: user.id,
      nationalId: user.nationalId,
      name: user.name,
      eventType: finalEventType,
      eventDate,
      eventDetails,
      eventLocation,
      approxAttendees,
      budget,
      contactPhone,
      specialReq,
      status: 'Agent will be in touch soon'
    };
    try {
      // POST new booking to API
      const response = await fetch(`${process.env.REACT_APP_API_URL}/bookings`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newBooking),
      });
      const data = await response.json();
      setBookings([...bookings, data]);
      // Reset form
      setEventType('');
      setEventDate('');
      setEventDetails('');
      setEventLocation('');
      setApproxAttendees('');
      setBudget('');
      setContactPhone('');
      setSpecialReq('');
      setCustomEventType('');
      setIsLoading(false);
      setShowModal(true);
    } catch (error) {
      console.error('Error creating booking:', error);
      setIsLoading(false);
    }
  };

  const handleDeleteBooking = (id) => {
    setDeletingBookingId(id);
    setShowDeleteModal(true);
  };

  const confirmDeleteBooking = async () => {
    if (!deletingBookingId) return;
    setDeleteLoading(true);
    setDeleteError('');
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/bookings/${deletingBookingId}`, {
        method: 'DELETE',
      });
      if (response.ok || response.status === 404) {
        // Re-fetch bookings to ensure UI is in sync with server
        const fetchResponse = await fetch(`${process.env.REACT_APP_API_URL}/bookings`);
        const allBookings = await fetchResponse.json();
        const userBookings = allBookings.filter(booking => booking.userId === user.id);
        setBookings(userBookings);
        // Close the modal and show success message
        setShowDeleteModal(false);
        setDeletingBookingId(null);
        setShowDeleteSuccess(true);
        setTimeout(() => {
          setShowDeleteSuccess(false);
        }, 3000);
      } else {
        // Handle other server errors (e.g., 500 Internal Server Error)
        const errorText = await response.text().catch(() => 'Unknown error'); // Try to get error message
        throw new Error(`Server error: ${response.status} - ${errorText || 'Failed to delete booking'}`);
      }
    } catch (error) {
      console.error('Error deleting booking:', error);
      setDeleteError(`Failed to delete booking: ${error.message}. Please try again.`);
      setShowDeleteModal(false);
    } finally {
      setDeleteLoading(false); // Always reset loading state
    }
  };

