import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaExclamationTriangle, FaCalendarAlt, FaMagic, FaFilm, FaTshirt, FaSmile, FaRocket, FaCode, FaPaintBrush, FaCrown, FaCloudRain, FaCar, FaSpinner } from 'react-icons/fa';
import './Dashboard.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [eventType, setEventType] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [eventDetails, setEventDetails] = useState('');
  const [eventLocation, setEventLocation] = useState('');
  const [approxAttendees, setApproxAttendees] = useState('');
  const [budget, setBudget] = useState('');
  const [contactPhone, setContactPhone] = useState('');
  const [specialReq, setSpecialReq] = useState('');
