            <p>This one's about context awareness. See live updates for your upcoming events.</p>
            <div className="event-widgets">
              {bookings.filter(booking => new Date(booking.eventDate) >= new Date()).map(booking => {
                // Fetch weather if not already fetched
                if (!weatherData[booking.id] && !weatherLoading[booking.id]) {
                  fetchWeather(booking.eventLocation, booking.eventDate, booking.id);
                }
                const weather = weatherData[booking.id];
                return (
                  <div key={booking.id} className="event-widget-card">
                    <h3>{booking.eventType} - {new Date(booking.eventDate).toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' })}, {new Date(booking.eventDate).toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' })}</h3>
                    <div className="widget-info">
                      <div className="weather-info">
                        {weatherLoading[booking.id] ? (
                          <>
                            <FaSpinner className="spinner" /> Loading weather...
                          </>
                        ) : weather ? (
                          weather.error ? (
                            <>
                              <FaCloudRain /> {weather.error}
                            </>
                          ) : (
                            <>
                              <img src={`https://openweathermap.org/img/wn/${weather.icon}.png`} alt={weather.description} style={{ width: '20px', height: '20px' }} />
                              {weather.temp}°C, {weather.description.charAt(0).toUpperCase() + weather.description.slice(1)}
                            </>
                          )
                        ) : (
                          <>
                            <FaCloudRain /> Weather data unavailable
                          </>
                        )}
                      </div>
                      <div className="traffic-info">
                        <FaCar /> Traffic congestion expected on Nairobi–Nakuru highway
                      </div>
                    </div>
                    <div className="suggestions">
                      <p><strong>Suggestion:</strong> {weather && !weather.error ? (
                        weather.description.toLowerCase().includes('rain') || weather.humidity > 70 ?
                        'Add tents to your equipment list or consider adjusting setup time by +30 mins.' :
                        'Weather looks good! No special preparations needed.'
                      ) : 'Check weather for suggestions.'}</p>
                    </div>
                  </div>
                );
              })}
              {bookings.filter(booking => new Date(booking.eventDate) >= new Date()).length === 0 && (
                <p>No upcoming events. Book an event to see weather and traffic updates!</p>
              )}
            </div>
          </div>
        )}

        {showModal && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h2>Booking Confirmed!</h2>
                <button className="close-modal" onClick={() => setShowModal(false)}>×</button>
              </div>
              <div className="modal-body">
                <p><strong>Snakepiece Event House Kenya</strong> is grateful for your trust in us!</p>
                <p>Your booked event has been sent successfully.</p>
                <p>An agent will be in touch with you in the next 24 hours with more details.</p>
                <div className="modal-icon">
                  <FaRocket />
                </div>
              </div>
              <div className="modal-footer">
                <button className="modal-btn" onClick={() => setShowModal(false)}>Close</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
